os=$(uname)

properties_file=$PWD/src/main/resources/app.properties

function checkProjectName(){
	local application_name_local=$1
	if [[ $application_name_local != "claroline" && $application_name_local != "addressbook" && $application_name_local != "ppma" \
		&& $application_name_local != "collabtive" && $application_name_local != "mrbs" && $application_name_local != "mantisbt" ]]; then
		echo Unknown application name: $application_name_local
		exit 1
	fi
}

function setClasspath(){
    local application_name_local=$1
    local application_classpath=
    if [[ $application_name_local == "claroline" ]]; then
        application_classpath=$claroline_classpath
    elif [[ $application_name_local == "addressbook" ]]; then
        application_classpath=$addressbook_classpath
    elif [[ $application_name_local == "ppma" ]]; then
        application_classpath=$ppma_classpath
    elif [[ $application_name_local == "collabtive" ]]; then
        application_classpath=$collabtive_classpath
    elif [[ $application_name_local == "mrbs" ]]; then
        application_classpath=$mrbs_classpath
    elif [[ $application_name_local == "mantisbt" ]]; then
        application_classpath=$mantisbt_classpath
    else
        echo Unknown application name: $application_name_local
        exit 1
    fi
    if [[ $os == "Darwin" ]]; then
        sed -i "" "s%project_classpath=.*$%project_classpath=$application_classpath%g" $properties_file
    else
        sed -i "s%project_classpath=.*$%project_classpath=$application_classpath%g" $properties_file
    fi
}

function setApplicationRelatedVariables(){
    local application_name_local=$1
    local current_application=$(grep project_name= $properties_file | awk -F'=' '{print $2}')
    if [[ $current_application != $application_name_local ]]; then
        if [[ $os == "Darwin" ]]; then
            sed -i "" "s/$current_application/$application_name_local/g" $properties_file
        else
            sed -i "s/$current_application/$application_name_local/g" $properties_file
        fi
    fi
}

#----------------------------------------------------------------------------------------------------------------------

application_name=$1

checkProjectName $application_name
setClasspath $application_name
setApplicationRelatedVariables $application_name
